import { 
    CHANGE_SEARCH_FIELD,
    REQUEST_CATS_PENDING,
    REQUEST_CATS_SUCCESS,
    REQUEST_CATS_FAILED
} from './constants.js';

const initialState = {
    searchField: ''
};

export const searchCats = (state=initialState, action={}) => {
    switch(action.type) {
        case CHANGE_SEARCH_FIELD:
            return Object.assign({}, state, { searchField: action.payload });
        default:
            return state;
    }
};

export const requestCats = (state=initialState, action={}) => {
    switch(action.type) {
        case REQUEST_CATS_PENDING:
            return Object.assing({}, state, { isPending: true });
        case REQUEST_CATS_SUCCESS:
            return Object.assign({}, state, { cats: action.payload });
    }
};
